
//this the example for while construct

package ControlStatement;

public class WhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 30;
		
		while(num>21) {
			System.out.println(num+" ");
			num = num-1;
		}
		System.out.println();
		System.out.println(num);
	}

}
